import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { ritualService } from '../services/ritualService';
import { trayService } from '../services/trayService';
import { useCart } from '../contexts/CartContext';

const RitualDetail = () => {
  const { id } = useParams();
  const [ritual, setRitual] = useState(null);
  const [trays, setTrays] = useState([]);
  const [loading, setLoading] = useState(true);
  const [trayLoading, setTrayLoading] = useState(true);
  const { addToCart } = useCart();

  useEffect(() => {
    fetchRitual();
    fetchTrays();
  }, [id]);

  const fetchRitual = async () => {
    try {
      const response = await ritualService.getRitualById(id);
      setRitual(response.data);
    } catch (error) {
      console.error('Error fetching ritual:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchTrays = async () => {
    try {
      const response = await trayService.getTraysByRitualId(id);
      setTrays(response.data);
    } catch (error) {
      console.error('Error fetching trays:', error);
    } finally {
      setTrayLoading(false);
    }
  };

  const handleAddToCart = (tray) => {
    addToCart(tray);
    // Show success message
    alert('Đã thêm vào giỏ hàng!');
  };

  const copyPrayer = () => {
    navigator.clipboard.writeText(ritual.prayerText);
    alert('Đã copy bài khấn!');
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-vietnam-red"></div>
      </div>
    );
  }

  if (!ritual) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-semibold text-gray-600 mb-4">Không tìm thấy lễ hội</h2>
          <Link to="/rituals" className="btn-primary">
            Quay lại danh sách
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-vietnam-cream py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Breadcrumb */}
        <nav className="mb-8">
          <Link to="/" className="text-vietnam-red hover:text-red-800">Trang chủ</Link>
          <span className="mx-2 text-gray-500">/</span>
          <Link to="/rituals" className="text-vietnam-red hover:text-red-800">Lễ hội</Link>
          <span className="mx-2 text-gray-500">/</span>
          <span className="text-gray-600">{ritual.name}</span>
        </nav>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2">
            {/* Ritual Header */}
            <div className="card mb-8">
              <div className="aspect-w-16 aspect-h-9 mb-6">
                <img
                  src={ritual.imageUrl || 'https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=800'}
                  alt={ritual.name}
                  className="w-full h-64 object-cover rounded-lg"
                />
              </div>
              <h1 className="text-3xl font-serif font-bold text-vietnam-red mb-4">
                {ritual.name}
              </h1>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                <div className="bg-vietnam-cream p-4 rounded-lg">
                  <h3 className="font-semibold text-vietnam-red mb-2">Ngày âm lịch</h3>
                  <p className="text-lg">{ritual.dateLunar}</p>
                </div>
                <div className="bg-vietnam-cream p-4 rounded-lg">
                  <h3 className="font-semibold text-vietnam-red mb-2">Ngày dương lịch</h3>
                  <p className="text-lg">
                    {ritual.dateSolar && ritual.dateSolar !== 'Tùy theo ngày khởi công' && ritual.dateSolar !== 'Tùy theo ngày sinh' 
                      ? new Date(ritual.dateSolar).toLocaleDateString('vi-VN')
                      : ritual.dateSolar || 'Chưa xác định'
                    }
                  </p>
                </div>
              </div>
              <p className="text-gray-700 text-lg leading-relaxed">
                {ritual.description || ritual.meaning}
              </p>
            </div>

            {/* Checklist */}
            <div className="card mb-8">
              <h2 className="text-2xl font-serif font-bold text-vietnam-red mb-6">
                Checklist đồ cúng
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {ritual.checklist?.map((item, index) => (
                  <div key={index} className="flex items-center space-x-3">
                    <input
                      type="checkbox"
                      id={`checklist-${index}`}
                      className="w-5 h-5 text-vietnam-red rounded focus:ring-vietnam-red"
                    />
                    <label htmlFor={`checklist-${index}`} className="text-gray-700">
                      {item}
                    </label>
                  </div>
                ))}
              </div>
            </div>

            {/* Prayer Text */}
            <div className="card mb-8">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-serif font-bold text-vietnam-red">
                  Bài khấn
                </h2>
                <button
                  onClick={copyPrayer}
                  className="btn-secondary"
                >
                  Copy bài khấn
                </button>
              </div>
              <div className="bg-vietnam-cream p-6 rounded-lg">
                <pre className="whitespace-pre-wrap text-gray-700 leading-relaxed font-serif">
                  {ritual.prayerText}
                </pre>
              </div>
            </div>
          </div>

          {/* Sidebar */}
          <div className="lg:col-span-1">
            {/* Suggested Trays */}
            <div className="card mb-8">
              <h2 className="text-2xl font-serif font-bold text-vietnam-red mb-6">
                Mâm cúng gợi ý
              </h2>
              {trayLoading ? (
                <div className="flex justify-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-vietnam-red"></div>
                </div>
              ) : trays.length > 0 ? (
                <div className="space-y-4">
                  {trays.map((tray) => (
                    <div key={tray.id} className="border border-gray-200 rounded-lg p-4">
                      <div className="aspect-w-16 aspect-h-9 mb-3">
                        <img
                          src={tray.imageUrl || 'https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=300'}
                          alt={tray.name}
                          className="w-full h-32 object-cover rounded-lg"
                        />
                      </div>
                      <h3 className="font-semibold text-vietnam-red mb-2">{tray.name}</h3>
                      <p className="text-sm text-gray-600 mb-2">{tray.description}</p>
                      <div className="flex justify-between items-center">
                        <span className="text-lg font-bold text-vietnam-red">
                          {tray.price.toLocaleString('vi-VN')} VNĐ
                        </span>
                        <button
                          onClick={() => handleAddToCart(tray)}
                          className="btn-primary text-sm px-4 py-2"
                        >
                          Thêm vào giỏ
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-gray-500 text-center py-8">
                  Chưa có mâm cúng nào cho lễ hội này
                </p>
              )}
            </div>

            {/* Related Rituals */}
            {ritual.relatedRituals && ritual.relatedRituals.length > 0 && (
              <div className="card">
                <h2 className="text-2xl font-serif font-bold text-vietnam-red mb-6">
                  Lễ hội liên quan
                </h2>
                <div className="space-y-2">
                  {ritual.relatedRituals.map((related, index) => (
                    <Link
                      key={index}
                      to={`/rituals?search=${encodeURIComponent(related)}`}
                      className="block p-3 bg-vietnam-cream rounded-lg hover:bg-vietnam-gold hover:text-vietnam-red transition-colors"
                    >
                      {related}
                    </Link>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default RitualDetail;
